      Group.hasMany(models.User)
      Group.belongsToMany(models.Role, { through: 'Group_Role', foreignKey: 'groupId' })
    }
  };
  Group.init({
    name: DataTypes.STRING,
    description: DataTypes.STRING,

  }, {
    sequelize,
    modelName: 'Group',
  });
  return Group;
};

