sourcesContent:
import db from "../models";

const createNewGroups = async () => {
    try {
        await db.GroupId.create({ ...data, password: hashPass })
        // copy li cc data, ring trng password th ghi  bng hashPass
        return {
            EM: 'Create User success',
            EC: 0,
            DT: [],
        }

    } catch (e) {
        console.log(e)
        return {
            EM: 'Error from createNewGroups service',
            EC: 1,
            DT: [],
        }
    }
}

module.exports = {
    createNewGroups
}

